import React, { useState, useEffect } from 'react';
import './Crash.css';

const Crash = () => {
  const [betAmount, setBetAmount] = useState(0);
  const [currentMultiplier, setCurrentMultiplier] = useState(1);
  const [isBettingEnabled, setIsBettingEnabled] = useState(true);
  const [isCrashed, setIsCrashed] = useState(false);

  useEffect(() => {
    let timeoutId;
    let increment = 0.05;
    let nextMultiplier = currentMultiplier + increment;

    const animateMultiplier = () => {
      timeoutId = setTimeout(() => {
        setCurrentMultiplier(nextMultiplier);
        increment += 0.05;
        nextMultiplier += increment;

        if (nextMultiplier >= 3.0) {
          clearInterval(timeoutId);
          setIsCrashed(true);
          setIsBettingEnabled(false);
        } else {
          animateMultiplier();
        }
      }, 100);
    };

    animateMultiplier();

    return () => {
      clearTimeout(timeoutId);
    };
  }, [currentMultiplier]);

  const handleBetAmountChange = (event) => {
    setBetAmount(event.target.value);
  };

  const handlePlaceBetClick = () => {
    console.log(`Placed bet with amount: ${betAmount}`);
    setIsBettingEnabled(false);
  };

  return (
    <div className="crash-container">
      <div className="multiplier-section">
        <div className="multiplier-label">Multiplier:</div>
        <div className="multiplier-value">{currentMultiplier.toFixed(2)}x</div>
        <div className={`crash-indicator ${isCrashed ? 'crashed' : ''}`}>CRASH!</div>
      </div>
      <div className="bet-section">
        <div className="bet-label">Place your bet:</div>
        <input
          type="number"
          min="0"
          max="100"
          step="1"
          value={betAmount}
          onChange={handleBetAmountChange}
          disabled={!isBettingEnabled}
        />
        <button onClick={handlePlaceBetClick} disabled={!isBettingEnabled}>
          Place Bet
        </button>
      </div>
    </div>
  );
};

export default Crash;
