import React, { useState } from "react";
import Graph from "./Graph";

const Crash = () => {
  const [sum, setSum] = useState(0);
  const [multiplier, setMultiplier] = useState(1);
  const [graphData, setGraphData] = useState([]);

  const handlePlaceBet = (bet) => {
    setSum(sum + bet);
    setMultiplier(multiplier + 0.1);
    setGraphData([...graphData, sum]);
  };

  return (
    <div className="crash">
      <div className="graph-container">
        <Graph data={graphData} />
        <div className="multiplier">{multiplier.toFixed(2)}x</div>
      </div>
      <div className="bet-container">
        <input type="number" onChange={(e) => setBet(e.target.value)} />
        <button onClick={() => handlePlaceBet(bet)}>Place Bet</button>
      </div>
    </div>
  );
};

export default Crash;
