import React, { useState } from "react";
import "../css/Roulette.css";

const Roulette = () => {
  const [result, setResult] = useState(null);
  const [spinning, setSpinning] = useState(false);

  const wheelSegments = [
    { value: 10, text: "10x" },
    { value: 0, text: "Nu ai castigat" },
    { value: 2, text: "2x" },
    { value: 0, text: "Nu ai castigat" },
    { value: 1, text: "1x" },
    { value: 15, text: "15x" },
    { value: 0, text: "Nu ai castigat" },
    { value: 0, text: "Nu ai castigat" },
  ];

  const handleSpin = () => {
    setSpinning(true);
    setResult(null);
    setTimeout(() => {
      const randomResult = Math.floor(Math.random() * wheelSegments.length);
      setResult(wheelSegments[randomResult]);
      setSpinning(false);
    }, 5000);
  };

  return (
    <div>
      <h1>Roulette</h1>
      <div className={`roulette ${spinning ? "spinning" : ""}`}>
        {result !== null ? (
          <div className="result">{result.text}</div>
        ) : (
          <div className="placeholder"></div>
        )}
        <div className="wheel">
          {wheelSegments.map((segment, index) => (
            <div
              key={index}
              className="segment"
              style={{
                transform: `rotate(${index * (360 / wheelSegments.length)}deg) skewY(-15deg)`,
                backgroundColor: index % 2 === 0 ? "#f00" : "#000",
              }}
            >
              <div className="segment-text">{segment.text}</div>
            </div>
          ))}
        </div>
      </div><br></br>
      <button disabled={spinning} onClick={handleSpin}>
        {spinning ? "Spinning..." : "Spin"}
      </button>
    </div>
  );
};

export default Roulette;
