import React,{ useState } from "react";
import "../css/Roulette.css";
import { Wheel } from 'react-custom-roulette'

const options = [
  { option: "10x", style: { backgroundColor: "#000000", textColor: "#fff" } },
  { option: "Nu ai castigat", style: { backgroundColor: "#e91e63", textColor: "#fff" } },
  { option: "2x", style: { backgroundColor: "#FF0000", textColor: "#fff" } },
  { option: "25x", style: { backgroundColor: "#000000", textColor: "#fff" } },
  { option: "Nu ai castigat", style: { backgroundColor: "#FF0000", textColor: "#fff" } },
  { option: "1x", style: { backgroundColor: "#FF0000", textColor: "#fff" } },
  { option: "Nu ai castigat", style: { backgroundColor: "#FF0000", textColor: "#fff" } },
  { option: "15x", style: { backgroundColor: "#000000", textColor: "#fff" } },
  { option: "7x", style: { backgroundColor: "#FF0000", textColor: "#fff" } },
];

function RoulettePage() {
  const [mustSpin, setMustSpin] = useState(false);
  const [prizeNumber, setPrizeNumber] = useState(0);

  const handleSpinClick = () => {
    if (!mustSpin) {
      const newPrizeNumber = Math.floor(Math.random() * options.length);
      setPrizeNumber(newPrizeNumber);
      setMustSpin(true);
    }
  }

  return (
    <>
      <div class='roulette'>
      <Wheel 
        mustStartSpinning={mustSpin}
        prizeNumber={prizeNumber}
        data={options}

        onStopSpinning={() => {
          setMustSpin(false);
        }}
      />
      </div>
      <button onClick={handleSpinClick}>SPIN</button>
    </>
  );
}

export default RoulettePage;
