import React, { useState } from "react";
import "../css/Roulette.css";

const Roulette = () => {
  const [result, setResult] = useState(null);
  const [spinning, setSpinning] = useState(false);

  const handleSpin = () => {
    setSpinning(true);
    setResult(null);
    setTimeout(() => {
      const randomResult = Math.floor(Math.random() * 8);
      setResult(randomResult);
      setSpinning(false);
    }, 5000);
  };

  return (
    <div>
      <h1>Roulette</h1>
      <div className={`roulette ${spinning ? "spinning" : ""}`}>
        <ul className="segments">
          <li className="segment ten-x">10x</li>
          <li className="segment no-win">Nu ai castigat</li>
          <li className="segment two-x">2x</li>
          <li className="segment no-win">Nu ai castigat</li>
          <li className="segment one-x">1x</li>
          <li className="segment fifteen-x">15x</li>
          <li className="segment no-win">Nu ai castigat</li>
          <li className="segment no-win">Nu ai castigat</li>
        </ul>
        {result !== null ? (
          <div className="result">{result}</div>
        ) : (
          <div className="placeholder"></div>
        )}
        <div className="wheel"></div>
      </div>
      <button disabled={spinning} onClick={handleSpin}>
        {spinning ? "Spinning..." : "Spin"}
      </button>
    </div>
  );
};

export default Roulette;
