import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import usersData from "../users.json";
import { Link } from "react-router-dom";

export const Register = (props) => {
  const history = useHistory();

  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    const newUser = { name, email, password };
    const existingUser = usersData.users.find(u => u.email === email);
    if (existingUser) {
      setMessage('This email address is already registered. Please use a different email address.');
    } else {
      usersData.users.push(newUser);
      setMessage('Registration successful! You can now log in.');
    }
  };

  return (
    <div className="auth-form-container">
      <h2>Register</h2>
      {message && <p>{message}</p>}
      <form className="register-form" onSubmit={handleSubmit}>
        <label htmlFor="name">Full name</label>
        <input value={name} onChange={(e) => setName(e.target.value)} type="text" placeholder="Full name" id="name" name="name" />
        <label htmlFor="email">Email</label>
        <input value={email} onChange={(e) => setEmail(e.target.value)} type="email" placeholder="youremail@gmail.com" id="email" name="email" />
        <label htmlFor="password">Password</label>
        <input value={password} onChange={(e) => setPassword(e.target.value)} type="password" placeholder="********" id="password" name="password" />
        <button type="submit">Register</button>
      </form>
      <button className="link-btn" onClick={() => history.push('/login')}>Already have an account? Login here.</button>
    </div>
  );
};

export default Register;
