import React, { useState } from "react";
import axios from "axios";
import users from "../users.json";

const Register = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");

  const handleNameChange = (event) => setName(event.target.value);
  const handleEmailChange = (event) => setEmail(event.target.value);
  const handlePasswordChange = (event) => setPassword(event.target.value);

  const handleSubmit = (event) => {
    event.preventDefault();
  
    const user = {
      name: name,
      email: email,
      password: password,
    };
  
    // Verificare dacă utilizatorul există deja în baza de date simulată
    const existingUser = users.find(u => u.email === email);
    if (existingUser) {
      setMessage("This email address is already registered. Please use a different email address.");
      return;
    }
  
    // Adăugare utilizator nou în baza de date simulată
    users.push(user);
    const json = JSON.stringify(users);
    const blob = new Blob([json], {type: "application/json"});
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.download = 'users.json';
    a.href = url;
    a.click();
  
    axios
      .post("http://localhost:3000/register", user)
      .then((response) => {
        console.log(response);
        setMessage(response.data.message);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  return (
    <div>
      <h1>Register</h1>
      {message && <p>{message}</p>}
      <form onSubmit={handleSubmit}>
        <label>
          Name:
          <input type="text" value={name} onChange={handleNameChange} />
        </label>
        <label>
          Email:
          <input type="email" value={email} onChange={handleEmailChange} />
        </label>
        <label>
          Password:
          <input type="password" value={password} onChange={handlePasswordChange} />
        </label>
        <button type="submit">Register</button>
      </form>
    </div>
  );
};

export default Register;
