import React, { useState } from "react";
import axios from "axios";
import usersData from "../users.json";
import { Link } from "react-router-dom";

const Register = (props) => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");

  const handleNameChange = (event) => setName(event.target.value);
  const handleEmailChange = (event) => setEmail(event.target.value);
  const handlePasswordChange = (event) => setPassword(event.target.value);

  const handleSubmit = (event) => {
    event.preventDefault();
  
    // Verificare dacă utilizatorul există deja în baza de date simulată
    const existingUser = usersData.users.find(u => u.email === email);
    if (existingUser) {
      setMessage("This email address is already registered. Please use a different email address.");
      return;
    }
  
    const user = {
      name: name,
      email: email,
      password: password,
    };
  
    axios
      .post("http://localhost:3000/register", user)
      .then((response) => {
        console.log(response);
        // Adăugare utilizator nou în baza de date simulată
        usersData.users.push(user);
        // Simulare logare cu utilizatorul nou creat
        console.log("Ai fost logat cu succes");
        setMessage(response.data.message);
      })
      .catch((error) => {
        console.log(error);
      });
    };

  return (
    <div className="auth-form-container">
      <h2>Register</h2>
      {message && <p>{message}</p>}
      <form className="register-form" onSubmit={handleSubmit}>
        <label htmlFor="name">Full name</label>
        <input value={name} onChange={handleNameChange} type="text" placeholder="Full name" id="name" name="name" />
        <label htmlFor="email">Email</label>
        <input value={email} onChange={handleEmailChange} type="email" placeholder="youremail@gmail.com" id="email" name="email" />
        <label htmlFor="password">Password</label>
        <input value={password} onChange={handlePasswordChange} type="password" placeholder="********" id="password" name="password" />
        <button type="submit">Register</button>
      </form>
      <button className="link-btn"><Link to="/login">Already have an account? Login here.</Link></button>
    </div>
  );
};

export default Register;
