import React, { useState, useEffect } from "react";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from "recharts";

const Crash = () => {
  const [data, setData] = useState([]);
  const [isRunning, setIsRunning] = useState(false);
  const [gameState, setGameState] = useState("waiting"); // "waiting", "playing", "crashed"
  const [currentBet, setCurrentBet] = useState(0);
  const [crashMultiplier, setCrashMultiplier] = useState(1);

  const updateData = () => {
    setData((prevData) => {
      const newData = [...prevData];
      const lastPoint = newData[newData.length - 1] || { x: 0, y: 1 };
      const nextPoint = {
        x: lastPoint.x + 1,
        y: lastPoint.y * (1 + Math.random() * 0.02), // Aici puteți ajusta coeficientul de creștere
      };
      newData.push(nextPoint);
      return newData;
    });
  };

  const startGame = () => {
    setData([{ x: 0, y: 1 }]);
    setIsRunning(true);
    setGameState("playing");
  };

  const cashOut = () => {
    setIsRunning(false);
    setGameState("crashed");
  };

  const resetGame = () => {
    setCurrentBet(0);
    setGameState("waiting");
    setCrashMultiplier(1);
  };

  useEffect(() => {
    if (isRunning) {
      const interval = setInterval(() => {
        updateData();
      }, 100); // Aici puteți ajusta viteza de actualizare a graficului (în milisecunde)
      return () => clearInterval(interval);
    }
  }, [isRunning]);

  useEffect(() => {
    const lastPoint = data[data.length - 1] || { x: 0, y: 1 };
    setCrashMultiplier(lastPoint.y);
  }, [data]);

  return (
    <div>
      <h1>Crash</h1>
      <p>Current bet: {currentBet} bits</p>
      {gameState === "waiting" && (
        <button onClick={startGame}>Start game</button>
      )}
      {gameState === "playing" && (
        <>
          <button onClick={cashOut}>Cash out</button>
          <ResponsiveContainer width="100%" height={300}>
            <LineChart data={data}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="x" />
              <YAxis />
              <Tooltip />
              <Line type="monotone" dataKey="y" stroke="#8884d8" />
            </LineChart>
          </ResponsiveContainer>
        </>
      )}
      {gameState === "crashed" && (
        <>
                    <p>Crash multiplier: {crashMultiplier.toFixed(2)}</p>
          <p>Win: {currentBet * crashMultiplier} bits</p>
          <button onClick={resetGame}>Play again</button>
        </>
      )}

      <div className="crash-bets">
        <button className="crash-bet" onClick={() => setCurrentBet(currentBet + 10)}>
          +10
        </button>
        <button className="crash-bet" onClick={() => setCurrentBet(currentBet + 100)}>
          +100
        </button>
        <button className="crash-bet" onClick={() => setCurrentBet(currentBet + 1000)}>
          +1000
        </button>
        <button className="crash-bet" onClick={() => setCurrentBet(currentBet + 10000)}>
          +10000
        </button>
        <button className="crash-bet" onClick={() => setCurrentBet(0)}>
          Clear
        </button>
      </div>
    </div>
  );
};

export default Crash;
